graph(time,nextGen_te,'yellow','te')


plot(0,0,'white',label = 'Legacy')
graph(time,legacy_ke,'g','ke')
graph(time,legacy_pe,'blue','pe')
graph(time,legacy_te,'purple','te')
legend(bbox_to_anchor=(1.004,.81), loc=2, ncol = 2,borderaxespad=0.)


#bottom plot
subplot(2,1,2)
title('frequency analysis')
plot(0,0,'white',label = 'next gen')
plotSpectrum(nextGen_ke,sample_rate,'ro','ke')
plotSpectrum(nextGen_pe,sample_rate,'orange','pe')
plotSpectrum(nextGen_te,sample_rate,'yellow','te')


plot(0,0,'white',label = 'Legacy')
plotSpectrum(legacy_ke ,sample_rate,'go','ke')
plotSpectrum(legacy_pe ,sample_rate,'blue','pe')
plotSpectrum(legacy_te ,sample_rate,'purple','te')
legend(bbox_to_anchor=(1.004,.81), loc=2, ncol = 2,borderaxespad=0.)


